const char index_html[] = "<!DOCTYPE html><html><head><title>NE555 Emulator</title></head><body><h1>Emulador 555</h1><form id='config'><label>Modo:</label><select name='mode'><option value='astable'>Astable</option><option value='monostable'>Monostable</option></select><br><label>R1 (Ohms):</label><input name='r1'><br><label>R2 (Ohms):</label><input name='r2'><br><label>C (Farads):</label><input name='c'><br><button type='submit'>Ejecutar</button><button type='button' onclick='stop()'>Detener</button></form><script>function stop(){fetch('/stop');}document.getElementById('config').onsubmit=function(e){e.preventDefault();var data=new FormData(this);fetch('/start',{method:'POST',body:new URLSearchParams(data)});};</script></body></html>";